import express, { NextFunction, Request, Response } from "express";
import "express-async-errors";
import { router } from "./routes";
import "./database/index";
import cors from "cors";

const app = express();

app.use(cors());
app.use(express.json());
app.use(router);
app.use(async (err: Error, req: Request, res: Response, next: NextFunction) => {
  if (err instanceof Error) {
    return res.status(400).json({
      error: err.message,
    });
  }

  return res.status(500).json({
    status: "error",
    message: "Internal Server Error",
  });
});

app.listen(3000);

export default app;

import { Router } from "express";
import CreateUserController from "./app/controllers/UserController/CreateUserController";

const router = Router();
router.get("/", async (req, res) => {
  return res.json({ msg: "Hello" });
});

router.get("/user/:id", CreateUserController.find);
router.get("/user", CreateUserController.all);
router.post("/user", CreateUserController.create);
router.delete("/user/:id", CreateUserController.delete);

export { router };

import { Request, Response } from "express";
import User from "../../models/User";
import mongoose from "mongoose";

class CreateUserController {
  async create(request: Request, response: Response) {
    try {
      const user = new User(request.body);
      user.setPassword(user.password);
      user.save();
      return response.status(201).json(user);
    } catch (err) {
      throw new Error(err.message);
    }
  }

  async all(request: Request, response: Response) {
    try {
      const all = await User.find();
      return response.status(200).json(all);
    } catch (err) {
      throw new Error(err.message);
    }
  }

  async find(request: Request, response: Response) {
    try {
      if (!mongoose.Types.ObjectId.isValid(request.params.id)) {
        throw new Error("Id invalid!");
      }
      const all = await User.findById(request.params.id);
      return response.json(all);
    } catch (err) {
      throw new Error(err.message);
    }
  }

  async delete(request: Request, response: Response) {
    try {
      if (!mongoose.Types.ObjectId.isValid(request.params.id)) {
        throw new Error("Id invalid!");
      }
      await User.findByIdAndDelete(request.params.id);
      return response.status(203).end();
    } catch (err) {
      throw new Error(err.message);
    }
  }
}

export default new CreateUserController();

import mongoose,{ Schema, Document, Model } from 'mongoose';
import bcrypt from 'bcryptjs';

export interface IUser extends Document {
  name: string; 
  username: string; 
  email: string; 
  password: string; 
};

interface IUserDocument extends IUser, Document {
  setPassword: (password: string) => Promise<void>
  checkPassword: (password: string) => Promise<boolean>
}

interface IUserModel extends Model<IUserDocument> {
  findByUsername: (username: string) => Promise<IUserDocument>
}

const UserSchema: Schema<IUserDocument> = new Schema({
  name: {
    type: String,
    required: true
  },
  username: {
    type: String,
    required: true
  },
  email: {
    type: String,
    require: true
  },
  password: {
    type: String,
    required: true
  }
})

UserSchema.methods.setPassword = async function (password: string) {
  const hash = await bcrypt.hash(password, 10)
  this.password = hash
}

UserSchema.methods.checkPassword = async function (password: string) {
  const result = await bcrypt.compare(password, this.password)
  return result
}

UserSchema.statics.findByUsername = function (username: string) {
  return this.findOne({ username })
}

const User = mongoose.model<IUserDocument, IUserModel>("User", UserSchema)
export default User

import mongoose from 'mongoose';

class Database {

  constructor() {
    this.mongo();
    console.log('connected on mongoose');
  }

  mongo() {
    const mongoURI = `mongodb://localhost:27017/node_api`;
     mongoose.connect(mongoURI, {
      useNewUrlParser: true,
      useFindAndModify: true,
      useUnifiedTopology: true
    });
  }
}
export default new Database();
